    /*
     * Listing of tbl_order
     */
    function index()
    {
        $params['limit'] = RECORDS_PER_PAGE; 
        $params['offset'] = ($this->input->get('per_page')) ? $this->input->get('per_page') : 0;
        
        $config = $this->config->item('pagination');
        $config['base_url'] = site_url('tbl_order/index?');
        $config['total_rows'] = $this->Tbl_order_model->get_all_tbl_order_count();
        $this->pagination->initialize($config);

        $data['tbl_order'] = $this->Tbl_order_model->get_all_tbl_order($params);
        
        $data['_view'] = 'order/index';
        $this->load->view('layouts/main',$data);
    }

    /*
