    /*
     * Editing a tbl_order
     */
    function edit($id_order)
    {   
        // check if the tbl_order exists before trying to edit it
        $data['tbl_order'] = $this->Tbl_order_model->get_tbl_order($id_order);
        
        if(isset($data['tbl_order']['id_order']))
        {
            $this->load->library('form_validation');

			$this->form_validation->set_rules('order_name','Order Name','required');
			$this->form_validation->set_rules('order_type','Order Type','required');
			$this->form_validation->set_rules('nama','Nama','required');
			$this->form_validation->set_rules('alamat','Alamat','required');
			$this->form_validation->set_rules('no_telp','No Telp','required');
			$this->form_validation->set_rules('email','Email','required|valid_email');
			$this->form_validation->set_rules('created_at','Created At','required');
			$this->form_validation->set_rules('keterangan','Keterangan','required');
		
			if($this->form_validation->run())     
            {   
                $params = array(
					'order_type' => $this->input->post('order_type'),
					'order_name' => $this->input->post('order_name'),
					'nama' => $this->input->post('nama'),
					'alamat' => $this->input->post('alamat'),
					'no_telp' => $this->input->post('no_telp'),
