    /*
     * Deleting tbl_order
     */
    function remove($id_order)
    {
        $tbl_order = $this->Tbl_order_model->get_tbl_order($id_order);

        // check if the tbl_order exists before trying to delete it
        if(isset($tbl_order['id_order']))
        {
            $this->Tbl_order_model->delete_tbl_order($id_order);
            redirect('tbl_order/index');
        }
        else
            show_error('The tbl_order you are trying to delete does not exist.');
